import styled from 'styled-components';
import { Link } from 'react-router-dom';
import { motion } from 'framer-motion';
import { useLocation } from 'react-router';

const Nav = () => {
	const { pathname } = useLocation();
	return (
		<StyledNav>
			<h1>
				<Link id='logo' to='/'>
					SPARTA
				</Link>
			</h1>
			<ul>
				<li>
					<Link to='/'>About Us</Link>
					<Line
						transition={{ duration: 0.75 }}
						initial={{ width: '0%' }}
						animate={{ width: pathname === '/' ? '50%' : '0%' }}></Line>
				</li>
				<li>
					<Link to='/work'>Our Work</Link>
					<Line
						transition={{ duration: 0.75 }}
						initial={{ width: '0%' }}
						animate={{ width: pathname === '/work' ? '50%' : '0%' }}></Line>
				</li>
				<li>
					<Link to='/contact'>Contact Us</Link>
					<Line
						transition={{ duration: 0.75 }}
						initial={{ width: '0%' }}
						animate={{ width: pathname === '/contact' ? '50%' : '0%' }}></Line>
				</li>
			</ul>
		</StyledNav>
	);
};

const StyledNav = styled.nav`
	min-height: 10vh;
	display: flex;
	margin: auto;
	justify-content: space-between;
	align-items: center;
	padding: 1rem 10rem;
	background: #282828;
	position: sticky;
	top: 0;
	z-index: 10;
	a {
		color: white;
		text-decoration: none;
	}
	ul {
		display: flex;
		list-style: none;
	}
	#logo {
		font-size: 3rem;
		font-family: 'Lobster', cursive;
		font-weight: lighter;
	}
	li {
		padding-left: 20rem;
		font-size: 2rem;
		position: relative;
	}
	@media (max-width: 1300px) {
		flex-direction: column;
		padding: 2rem 1rem;
		#logo {
			display: inline-block;
			margin: 2rem;
		}

		ul {
			padding: 2rem;
			justify-content: space-around;
			width: 100%;
		}
		li {
			padding: 0;
		}
	}
`;

const Line = styled(motion.div)`
	height: 0.3rem;
	background: #23d997;
	width: 0%;
	position: absolute;
	bottom: -80%auto;
	left: 60%;
	@media (max-width: 1300px) {
		left: 0%;
	}
`;

export default Nav;
